// Import necessary components and styles

export const Todo: FC<TodoProps> = ({
  name,
  status,
  todoId
}) => {
  const [isEditTodo, setIsEditTodo] = useState(false)
  const [isDeleteModalVisible, setIsDeleteModalVisible] = useState(false)
  const [isHovered, setIsHovered] = useState(false)

  const dispatch = useDispatch()
  const [form] = Form.useForm()
  const { maxTodoLength } = useSelector((state: RootState) => state.todos)

  const handleUpdate = useCallback(
    (data: ITodo) => {
      dispatch(updateTodo(data))
      setIsEditTodo(false)
    },
    [dispatch]
  )

  const handleSubmit = useCallback(() => {
    form
      .validateFields()
      .then(values => {
        handleUpdate({ id: todoId, name: values.name, status })
      })
      .catch(error => {
        console.error("Validation failed:", error)
      })
  }, [form, handleUpdate, todoId, status])

  const handleEdit = (): void => {
    setIsEditTodo(true)
    form.setFieldsValue({ name })
  }

  const handleDelete = (): void => {
    setIsDeleteModalVisible(true)
  }

  const handleConfirmDelete = (): void => {
    dispatch(deleteTodo({ id: todoId }))
    status === TodoStatus.COMPLETED
      ? dispatch(decrementCompleted())
      : dispatch(decrementNotCompleted())
    setIsDeleteModalVisible(false)
  }

  const handleCancelDelete = (): void => {
    setIsDeleteModalVisible(false)
  }

  const handleMouseEnter = (): void => {
    setIsHovered(true)
  }

  const handleMouseLeave = (): void => {
    setIsHovered(false)
  }

  const onChange: CheckboxProps['onChange'] = (e): void => {
    const updatedStatus = e.target.checked ? TodoStatus.COMPLETED : TodoStatus.NOT_COMPLETED
    dispatch(updateTodo({ id: todoId, status: updatedStatus }))
  }

  return (
    <>
      <Space
        direction="horizontal"
        size="middle"
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        style={{
          display: 'flex',
          alignItems: "center",
          padding: "16px",
          marginBottom: '12px',
          border: "1px solid #ccc",
          borderRadius: "8px",
          background: "#e6f7ff",
          width: "400px"
        }}
      >
        {isEditTodo ?
          <Form
            form={form}
            name="validateOnly"
            layout="vertical"
            autoComplete="off"
            onFinish={handleSubmit}
          >
            <Form.Item
              label="Name"
              name="name"
              rules={[
                {
                  required: true,
                  message: "Please enter todo name",
                },
                {
                  max: maxTodoLength,
                  message: `Todo text exceeds the maximum allowed length of ${maxTodoLength} characters`,
                },
              ]}
            >
              <Input />
            </Form.Item>

            <Button type="primary" htmlType="submit">
              Save
            </Button>
            <Button onClick={() => setIsEditTodo(false)}>
              Cancel
            </Button>
          </Form>
          :
          <>
            <Checkbox onChange={onChange} checked={status === TodoStatus.COMPLETED} />
            <Typography.Title level={5} style={{ color: "#1890ff", margin: 0 }}>
              {name}
            </Typography.Title>
            {isHovered &&
              <Space wrap>
                <Button type="link" onClick={handleEdit}>
                  <EditOutlined style={{ color: "#1890ff", fontSize: "16px" }} />
                </Button>
                <Button type="link" onClick={handleDelete}>
                  <DeleteOutlined style={{ color: "#ff4d4f", fontSize: "16px" }} />
                </Button>
              </Space>
            }
          </>
        }
      </Space>
      <DeleteConfirmationModal
        isVisible={isDeleteModalVisible}
        onCancel={handleCancelDelete}
        onConfirm={handleConfirmDelete}
        modalInformation="Are you sure you want to delete this todo?"
      />
    </>
  )
}
