import { FC, useCallback, useEffect, useRef, useState } from "react"
import { Button, Space, Typography, Form, Checkbox, CheckboxProps, Input, Row, Col } from "antd"
import { CheckOutlined, CloseOutlined, DeleteOutlined, EditOutlined } from "@ant-design/icons"
import { ITodo } from "../../types/todo"
import { TodoStatus } from "../../types/status"
import { deleteTodo, updateTodo } from "../../redux/TodoReducer"
import { useDispatch, useSelector } from "react-redux"
import { DeleteConfirmationModal } from "../DeleteConfirmationModal"
import { RootState } from "../../redux"
import { decrementCompleted, decrementNotCompleted } from "../../redux/CounterReducer"
import { AddEditTodo } from "../AddEditTodo"


interface TodoProps {
  todoId: string
  name: string
  status: TodoStatus
}

export const Todo: FC<TodoProps> = ({
  name,
  status,
  todoId
}) => {
  const [isEditTodo, setIsEditTodo] = useState(false)
  const [isDeleteModalVisible, setIsDeleteModalVisible] = useState(false)
  const [isHovered, setIsHovered] = useState(false)
  const dispatch = useDispatch()
  const [form] = Form.useForm()

  const handleEdit = (): void => {
    setIsEditTodo(true)
    form.setFieldsValue({
      name
    })
  }

  const handleDelete = (): void => {
    setIsDeleteModalVisible(true)
  }

  const handleConfirmDelete = (): void => {
    dispatch(deleteTodo({ id: todoId }))
    status === TodoStatus.COMPLETED ? dispatch(decrementCompleted()) : dispatch(decrementNotCompleted())
    setIsDeleteModalVisible(false)
  }

  const handleCancelDelete = (): void => {
    setIsDeleteModalVisible(false)
  }

  const handleMouseEnter = (): void => {
    setIsHovered(true)
  }

  const handleMouseLeave = (): void => {
    setIsHovered(false)
  }

  // const hadleCancelEdit = (): void => {
  //   setIsEditTodo(false)
  // }

  const onChange: CheckboxProps['onChange'] = (e): void => {
    const status = e.target.checked ? TodoStatus.COMPLETED : TodoStatus.NOT_COMPLETED
    dispatch(updateTodo({ id: todoId, status }))
  }

  // const handleSubmit = (data: { data: Partial<ITodo> }): void => {
  //   dispatch(updateTodo({ id: todoId, ...data }))
  //   setIsEditTodo(false)
  // }

  const handleSubmit = useCallback((data: any): void => {
    console.log("Submit edit")

    console.log({ data })
    dispatch(updateTodo({ id: todoId, ...data }))
    setIsEditTodo(false)
  }, [])

  // const handleOnCreate = useCallback((data: Omit<ITodo, 'id'>) => {
  //   const status = data.status
  //   status === TodoStatus.NOT_COMPLETED ? dispatch(incrementNotCompleted()) : dispatch(incrementCompleted())
  //   dispatch(addTodo(data))
  // }, [])

  return (
    <>
      <Space style={{ height: "40px" }}>
        {isEditTodo ?
          <AddEditTodo setIsEditMode={setIsEditTodo} onSubmit={handleSubmit} initialValues={{ name: name }} />
          :
          <Space direction="horizontal" size="middle" id="333333"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            <Checkbox onChange={onChange} checked={status === TodoStatus.COMPLETED} />
            <Typography.Title
              level={5}
              style={{ color: "#1890ff", margin: 0 }}
            >
              {name}
            </Typography.Title>
            {isHovered &&
              <Space style={{ height: '24px' }} >
                <Button type="link" onClick={handleEdit} icon={<EditOutlined style={{ color: "#1890ff", fontSize: "16px", transition: "0.5s ease-in" }} />} />
                <Button type="link" onClick={handleDelete}>
                  <DeleteOutlined style={{ color: "#ff4d4f", fontSize: "16px" }} />
                </Button>
              </Space>
            }
          </Space>}
      </Space >
      <DeleteConfirmationModal
        isVisible={isDeleteModalVisible}
        onCancel={handleCancelDelete}
        onConfirm={handleConfirmDelete}
        modalInformation="Are you sure you want to delete this todo?"
      />
    </>
  )
}
