import { createSlice, PayloadAction } from "@reduxjs/toolkit"
import { ITodo } from "../types/todo"
import { generateId } from "../helpers/generateId"

const initialState: TodosInitialState = {
  todos: [],
  maxTodoLength: 50,
}

export const todoSlice = createSlice({
  name: 'todo',
  initialState,
  reducers: {
    addTodo: (state, action: PayloadAction<Omit<ITodo, 'id'>>) => {
      const { text } = action.payload

      // Check if the length of the todo's text is within the allowed limit
      if (text.length <= state.maxTodoLength) {
        const newTodo = { ...action.payload, id: generateId() }
        state.todos = [...state.todos, newTodo]
      } else {
        // Handle the case where the text length exceeds the limit (you can show an error, etc.)
        console.error(`Todo text exceeds the maximum allowed length of ${state.maxTodoLength} characters.`)
      }
    },
    updateTodo: (state, action: PayloadAction<Partial<ITodo>>) => {
      const updatedTodoIndex = state.todos.findIndex(el => el.id === action.payload.id)

      if (updatedTodoIndex !== -1) {
        state.todos[updatedTodoIndex] = { ...state.todos[updatedTodoIndex], ...action.payload }
      }
    },
    deleteTodo: (state, action: PayloadAction<{ id: string }>) => {
      const { id } = action.payload
      state.todos = state.todos.filter(el => el.id !== id)
    },
  },
})

export interface TodosInitialState {
  todos: ITodo[]
  maxTodoLength: number
}

export const { addTodo, updateTodo, deleteTodo } = todoSlice.actions
export default todoSlice.reducer
