import { Dispatch, FC, SetStateAction, useCallback } from "react"
import { Button, Form, Input, Space, Modal, Select, Col, Row } from "antd"
import { ITodo } from "../../types/todo"
import { RootState } from "../../redux"
import { useSelector } from "react-redux"

interface AddTodoProps {
  setIsEditMode: Dispatch<SetStateAction<boolean>>
  onSubmit: (data: ITodo) => void
  visible: boolean
  title?: string
  todoId?: string
}

export const AddEditTodo: FC<AddTodoProps> = ({
  setIsEditMode,
  onSubmit,
  visible,
  title = "Add Todo",
  todoId,
}) => {
  const [form] = Form.useForm()
  const { maxTodoLength } = useSelector((state: RootState) => state.todos)

  const handleSubmit = useCallback(
    (data: ITodo) => {
      const todoWithId = todoId ? { ...data, id: todoId } : { ...data }
      onSubmit(todoWithId)
      form.resetFields()
      setIsEditMode(false)
    },
    [form, onSubmit, setIsEditMode, todoId]
  )

  const handleCancel = (): void => {
    form.resetFields()
    setIsEditMode(false)
  }

  return (
    <Modal
      title={title}
      visible={visible}
      onCancel={handleCancel}
      footer={null}
      destroyOnClose
    >
      <Form
        form={form}
        name="validateOnly"
        layout="vertical"
        autoComplete="off"
        onFinish={handleSubmit}
      >
        <Form.Item
          label="Name"
          name="name"
          rules={[
            {
              required: true,
              message: "Please enter todo name",
            },
            {
              max: maxTodoLength,
              message: `Todo text exceeds the maximum allowed length of ${maxTodoLength} characters`,
            },
          ]}
        >
          <Input />
        </Form.Item>

        <Space style={{ justifyContent: "flex-end", marginTop: "8px" }}>
          <Button type="primary" htmlType="submit">
                        Submit
          </Button>
          <Button
            type="primary"
            onClick={handleCancel}
            style={{ backgroundColor: "#faad14", borderColor: "#faad14" }}
          >
                        Cancel
          </Button>
        </Space>
      </Form>
    </Modal>
  )
}
