import { Dispatch, FC, SetStateAction, useCallback } from "react"
import { Button, Form, Input, Space, Modal, Select } from "antd"
import { ITodo } from "../../types/todo"
import { TodoStatus } from "../../types/status"
import { useSelector } from "react-redux"
import { RootState } from "../../redux"

interface AddTodoProps {
  setIsEditMode: Dispatch<SetStateAction<boolean>>
  onSubmit: (data: ITodo) => void
  visible: boolean
  title?: string
  todoId?: string

}

export const AddTodo: FC<AddTodoProps> = ({
  setIsEditMode,
  onSubmit,
  visible,
  title = "Add Todo",
  todoId
}) => {
  const [form] = Form.useForm()
  const { maxTodoLength } = useSelector((state: RootState) => state.todos)


  const handleSubmit = useCallback(
    (data: ITodo) => {
      const todoWithId = todoId ? { ...data, id: todoId } : { ...data }
      onSubmit(todoWithId)
      form.resetFields()
      setIsEditMode(false)
    },
    [form, onSubmit, setIsEditMode]
  )

  const handleCancel = (): void => {
    form.resetFields()
    setIsEditMode(false)
  }

  return (
    <Modal
      title={title}
      open={visible}
      footer={null}
      onCancel={handleCancel}
      destroyOnClose
    >
      <Form
        form={form}
        name="validateOnly"
        layout="vertical"
        autoComplete="off"
        onFinish={handleSubmit}
      >
        <Form.Item
          label="Name"
          name="name"
          rules={[
            {
              required: true,
              message: "Please enter todo name",
            },
            {
              max: maxTodoLength,
              message: "Please enter todo name",
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label="Status"
          name="status"
          rules={[
            {
              required: true,
              message: "Please select todo status",
            },
          ]}
        >
          <Select
            defaultValue={form.getFieldValue("status")}
            onChange={(value: TodoStatus): void => {
              form.setFieldsValue({ status: value })
            }} options={[
              { value: TodoStatus.NOT_COMPLETED, label: 'Not completed' },
              { value: TodoStatus.COMPLETED, label: 'Completed' },
            ]}
          />
        </Form.Item>
        <Space style={{ justifyContent: "flex-end", marginTop: "8px" }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
          <Button
            type="primary"
            onClick={handleCancel}
            style={{ backgroundColor: "#faad14", borderColor: "#faad14" }}
          >
            Cancel
          </Button>
        </Space>
      </Form>
    </Modal >
  )
}
