import React, { useCallback, useState } from "react"
import {
  Col,
  Row,
  Space,
  Typography,
  Layout,
  List,
  Radio,
  Button,
} from "antd"
import { useDispatch, useSelector } from "react-redux"
import { RootState } from "../../redux"
import { TodoStatus } from "../../types/status"
import { Content } from "antd/es/layout/layout"
import { getFilteredTodos } from "../../helpers/getFilteredTodos"
import { FilterTypes } from "../../types/filterTypes"
import { Todo } from "../../components/Todo"
import { addTodo } from "../../redux/TodoReducer"
import { ITodo } from "../../types/todo"
import { incrementCompleted, incrementNotCompleted } from "../../redux/CounterReducer"
import { AddEditTodo } from "../../components/AddEditTodo"

const { Title } = Typography

export const TodoList: React.FC = () => {
  const { todos } = useSelector((state: RootState) => state.todos)
  const { completedTodos, notCompletedTodos } = useSelector((state: RootState) => state.counter)
  const [filterType, setFilterType] = useState(FilterTypes.ALL)
  const [isEditMode, setIsEditMode] = useState(false)
  const dispatch = useDispatch()

  const handleOnAddTodo = (): void => {
    setIsEditMode(true)
  }

  const handleOnCreate = useCallback((data: any) => {
    const status = data.status
    status === TodoStatus.NOT_COMPLETED ? dispatch(incrementNotCompleted()) : dispatch(incrementCompleted())
    dispatch(addTodo(data))
  }, [])


  return (
    <Layout style={{ height: '100vh', }}>
      <Content style={{
        padding: 40,
        display: 'flex',
        flexDirection: "column"
      }}>
        <Space direction="horizontal" align="center">
          <Title level={2} type="secondary" >
            Todo List
          </Title>
        </Space>
        <Row align="middle" style={{ marginBottom: '24px' }}>
          <Col span={12}>
            <Space direction="horizontal" >
              <Radio.Group
                onChange={(e): void => setFilterType(e.target.value)}
                value={filterType}
              >
                <Radio.Button value={FilterTypes.ALL}>All ({completedTodos + notCompletedTodos})</Radio.Button>
                <Radio.Button value={FilterTypes.COMPLETED}>Completed ({completedTodos})</Radio.Button>
                <Radio.Button value={FilterTypes.NOT_COMPLETED}>Not Completed ({notCompletedTodos})</Radio.Button>
              </Radio.Group>
            </Space>
          </Col>
          {filterType !== FilterTypes.NOT_COMPLETED && <Col span={12} style={{ display: "flex", justifyContent: "end" }}>
            <Button
              type="primary"
              onClick={handleOnAddTodo}
              style={{
                width: "50px",
              }}
            >
              +
            </Button>
          </Col>}
        </Row>
        {isEditMode && <AddEditTodo
          visible={isEditMode}
          setIsEditMode={setIsEditMode}
          onSubmit={handleOnCreate}
          title="Add Todo"
        />}
        <List
          dataSource={getFilteredTodos(todos, filterType)}
          renderItem={({ id, name, status }): React.ReactNode => {
            return (
              <List.Item key={id}>
                <Todo todoId={id}
                  name={name}
                  status={status} />
              </List.Item>
            )
          }}
        />

      </Content>
    </Layout>
  )
}
