import { Dispatch, FC, SetStateAction, useCallback } from "react"
import { Button, Form, Input, Space, Modal, Select, Col, Row, Card } from "antd"
import { ITodo } from "../../types/todo"
import { TodoStatus } from "../../types/status"
import { useSelector } from "react-redux"
import { RootState } from "../../redux"
import TextArea from "antd/es/input/TextArea"
import { CheckOutlined, CloseOutlined } from "@ant-design/icons"

interface AddTodoProps {
    setIsEditMode: Dispatch<SetStateAction<boolean>>
    onSubmit: (data: ITodo) => void
    initialValues?: { title: string, description: string }
    visible: boolean
    title?: string
    //   todoId?: string

}

export const AddEditTodo: FC<AddTodoProps> = ({
    setIsEditMode,
    onSubmit,
    visible,
    title = "Add Todo",
    //   todoId
}) => {
    const [form] = Form.useForm()
    const { maxTodoLength } = useSelector((state: RootState) => state.todos)

    const handleSubmit = useCallback(
        (data: any) => {
            //   const todoWithId = todoId ? { ...data, id: todoId } : { ...data }
            onSubmit(data)
            form.resetFields()
            setIsEditMode(false)
        },
        [form, onSubmit, setIsEditMode]
    )

    const handleCancel = (): void => {
        form.resetFields()
        setIsEditMode(false)
    }

    useEffect(() => {
        if (initialValues) {
            form.setFieldsValue(initialValues)
        }
    }, [initialValues, form])

    return (
        <Row
            //   gutter={16} align="middle"
            // onMouseEnter={handleMouseEnter}
            // onMouseLeave={handleMouseLeave}
            style={{
                // display: 'flex',
                // alignItems: "center",
                padding: "16px",
                // marginBottom: '12px',
                border: "1px solid #ccc",
                borderRadius: "8px",
                background: "#e6f7ff",
                width: "400px",
                height: "100px"
            }
            }
        >
            <Form
                form={form}
                name="validateOnly"
                // layout="vertical"
                autoComplete="off"
                onFinish={handleSubmit}
                style={{ width: '100%' }}
            >
                <Row gutter={16} align="top" style={{ width: '100%' }}>
                    <Col span={16} flex="auto">
                        <Form.Item
                            label="Name"
                            name="name"
                            rules={[
                                {
                                    required: true,
                                    message: "Please enter todo name",
                                },
                                {
                                    max: maxTodoLength,
                                    message: `Todo text exceeds the maximum allowed length of ${maxTodoLength} characters`,
                                },
                            ]}
                        >
                            <Input />
                        </Form.Item>
                    </Col>
                    <Col span={6}>
                        <Space >
                            <Button type="primary" htmlType="submit" icon={< CheckOutlined />} style={{ backgroundColor: "#faad14", borderColor: "#faad14" }}
                            />
                            <Button
                                type="primary"
                                onClick={handleCancel}
                                style={{ backgroundColor: "#faad14", borderColor: "#faad14" }}
                                icon={<CloseOutlined />}
                            />
                        </Space>
                    </Col>
                </Row>
            </Form>
            {/* </Col> */}

        </Row >
    )
}
