import React, { useEffect, useState } from "react"
import {
  Col,
  Form,
  Row,
  Space,
  Typography,
  Button,
  Divider,
  Card as BasicCard,
  Spin,
} from "antd"
import { DeleteOutlined, EditOutlined } from "@ant-design/icons"
import { Card } from "../../components/Card"
import { useSelector } from "react-redux"
import { RootState } from "../../redux"
import { TodoStatus } from "../../types/status"
// import { Card } from "../Card";
// import { DeleteOutlined, EditOutlined } from "@ant-design/icons";
// import { useUpdateBoard } from "../../hooks/useUpdateBoard";
// import { ITodo } from "../../types";
// import { useDeleteBoard } from "../../hooks/useDeleteBoard";
// import { TodoStatus } from "../../types/status";
// import { AddEditBoardTitle } from "../AddEditBoardTile";
// import { useUpdateTodo } from "../../hooks/useUpdateTodo";
// import { getTodosByStatus } from "../../helpers/getTodosByStatus";
// import { DeleteConfirmationModal } from "../DeleteConfirmationModal/DeleteConfirmationModal";

const { Title } = Typography

export const TodoList: React.FC = () => {



  //   if (isLoadingUpdate || isLoadingDelete) {
  //     return <Spin size="large" />;
  //   }


  const { todos } = useSelector((state: RootState) => state.todos)

  return (
    <div
      style={{
        display: "flex",
        flex: "1",
        flexDirection: "column",
        height: "100%",
      }}
    >
      <>
        <Space direction="horizontal" align="center">
          <Title level={3} underline style={{ margin: 0 }}>
            Todo List
          </Title>
        </Space>
        <Divider style={{ margin: "8px 0" }} />
      </>
      <Row gutter={[16, 16]}>
        <Col xs={24} sm={12} md={8}>
          <Card
            title="NOT COMPLETED"
            todos={todos?.filter(el => el.status === TodoStatus.NOT_COMPLETED)}
            status={TodoStatus.NOT_COMPLETED}
          />
        </Col>
        <Col xs={24} sm={12} md={8}>
          <Card
            title="COMPLETED"
            todos={todos?.filter(el => el.status === TodoStatus.COMPLETED)}
            status={TodoStatus.COMPLETED}
          />
        </Col>
      </Row>
    </div>
  )
}
