import React, { useCallback, useState } from "react"
import {
  Col,
  Row,
  Space,
  Typography,
  Divider,
  Layout,
  List,
  Radio,
  Button,
  InputNumber,
  InputNumberProps,
} from "antd"
import { Card } from "../../components/Card"
import { useDispatch, useSelector } from "react-redux"
import { RootState } from "../../redux"
import { TodoStatus } from "../../types/status"
import { Content } from "antd/es/layout/layout"
import { getFilteredTodos } from "../../helpers/getFilteredTodos"
import { FilterTypes } from "../../types/filterTypes"
import { Todo } from "../../components/Todo"
import { addTodo } from "../../redux/TodoReducer"
import { ITodo } from "../../types/todo"
import { AddEditTodo } from "../../components/AddTodo"
import { incrementCompleted, incrementNotCompleted } from "../../redux/CounterReducer"

const { Title } = Typography

export const TodoList: React.FC = () => {
  const { todos, maxTodoLength } = useSelector((state: RootState) => state.todos)
  const { completedTodos, notCompletedTodos } = useSelector((state: RootState) => state.counter)
  const [filterType, setFilterType] = useState(FilterTypes.ALL)
  const [isEditMode, setIsEditMode] = useState(false)
  const dispatch = useDispatch()

  const handleOnAddTodo = (): void => {
    setIsEditMode(true)
  }

  const handleOnCreate = useCallback((data: Omit<ITodo, 'id'>) => {
    const status = data.status
    status === TodoStatus.NOT_COMPLETED ? dispatch(incrementNotCompleted()) : dispatch(incrementCompleted())
    dispatch(addTodo(data))
  }, [])

  const handleOnChangeLength: InputNumberProps['onChange'] = (e): void => {
    console.log("change length")
    console.log(e)
  }

  return (
    <Layout style={{ height: '100vh', }}>
      <Content style={{
        padding: 40,
        display: 'flex',
        flexDirection: "column"
      }}>
        <Space direction="horizontal" align="center">
          <Title level={2} type="secondary" >
            Todo List
          </Title>
          <Divider />
        </Space>
        <Space direction="horizontal" style={{ marginBottom: '24px' }} >
          <Radio.Group
            onChange={(e): void => setFilterType(e.target.value)}
            value={filterType}
          >
            <Radio.Button value={FilterTypes.ALL}>All</Radio.Button>
            <Radio.Button value={FilterTypes.COMPLETED}>Completed</Radio.Button>
            <Radio.Button value={FilterTypes.NOT_COMPLETED}>Not Completed</Radio.Button>
          </Radio.Group>
        </Space>
        <Space wrap>
          <Typography >Completed: {completedTodos}</Typography>
          <Typography >Not Completed: {notCompletedTodos}</Typography>
        </Space>
        <InputNumber min={1} max={500} defaultValue={maxTodoLength} placeholder="Enter max lenght of one todo" onChange={handleOnChangeLength} />

        <List
          dataSource={getFilteredTodos(todos, filterType)}
          renderItem={({ id, name, status }): React.ReactNode => {
            return (
              <List.Item key={id}>
                <Todo todoId={id}
                  name={name}
                  status={status} />
              </List.Item>
            )
          }}
        />
        <Button
          type="primary"
          onClick={handleOnAddTodo}
          style={{
            width: "120px",
            // position: "absolute",
            // top: "8px",
            // right: "8px",
            // backgroundColor: "#1890ff",
            // borderColor: "#1890ff",
            // color: "#fff",
          }}
        >
          Add Todo
        </Button>
        <AddEditTodo
          visible={isEditMode}
          setIsEditMode={setIsEditMode}
          onSubmit={handleOnCreate}
          title="Add Todo"
        />
        ///
        <Row gutter={[16, 16]}>
          <Col xs={24} sm={12} md={12}>
            <Card
              title="NOT COMPLETED"
              todos={todos?.filter(el => el.status === TodoStatus.NOT_COMPLETED)}
              status={TodoStatus.NOT_COMPLETED}
            />
          </Col>
          <Col xs={24} sm={12} md={12}>
            <Card
              title="COMPLETED"
              todos={todos?.filter(el => el.status === TodoStatus.COMPLETED)}
              status={TodoStatus.COMPLETED}
            />
          </Col>
        </Row>
      </Content>
    </Layout>
  )
}
