import { createSlice, PayloadAction } from "@reduxjs/toolkit"

interface CounterState {
  completedTodos: number
  notCompletedTodos: number
}

const initialState: CounterState = {
  completedTodos: 0,
  notCompletedTodos: 0,
}

export const counterSlice = createSlice({
  name: 'counter',
  initialState,
  reducers: {
    incrementCompleted: (state) => {
      state.completedTodos += 1
    },
    decrementCompleted: (state) => {
      state.completedTodos = Math.max(0, state.completedTodos - 1)
    },
    incrementNotCompleted: (state) => {
      state.notCompletedTodos += 1
    },
    decrementNotCompleted: (state) => {
      state.notCompletedTodos = Math.max(0, state.notCompletedTodos - 1)
    },
  },
})

export const { incrementCompleted, decrementCompleted, incrementNotCompleted, decrementNotCompleted } = counterSlice.actions
export default counterSlice.reducer
