import { FC, useCallback, useState } from "react"
import { Button, Space, Typography, Form } from "antd"
import { DeleteOutlined, EditOutlined } from "@ant-design/icons"
// import { useDeleteTodo } from "../../hooks/useDeleteTodo"
// import { useUpdateTodo } from "../../hooks/useUpdateTodo"
// import { ITodo } from "../../types"
import { AddEditTodo } from "../AddEditTodo"
import { ITodo } from "../../types/todo"
import { TodoStatus } from "../../types/status"

// import { DeleteConfirmationModal } from "../DeleteConfirmationModal/DeleteConfirmationModal"

interface TodoProps {
  // todoId: string
  // boardId: string
  name: string
  status: TodoStatus
  // index: number
  // description: string
}

export const Todo: FC<TodoProps> = ({
  name,
  status,
}) => {
  const [isEditTodo, setIsEditTodo] = useState(false)
  const [isDeleteModalVisible, setIsDeleteModalVisible] = useState(false)
  // const { remove } = useDeleteTodo(boardId)
  // const { update } = useUpdateTodo()
  const [form] = Form.useForm()

  const handleUpdate = useCallback(
    (data: ITodo) => {
      // update(todoId, data)
      setIsEditTodo(false)
    },
    []
  )

  const handleEdit = (): void => {
    setIsEditTodo(true)
    form.setFieldsValue({
      name,
    })
  }

  const handleDelete = (): void => {
    setIsDeleteModalVisible(true)
  }

  const handleConfirmDelete = (): void => {
    // remove(todoId)
    setIsDeleteModalVisible(false)
  }

  const handleCancelDelete = (): void => {
    setIsDeleteModalVisible(false)
  }

  return (
    <>
      <AddEditTodo
        title="Edit Todo"
        status={status}
        visible={isEditTodo}
        setIsEditMode={setIsEditTodo}
        onSubmit={handleUpdate}
        initialValues={{ name, status }}
      />
      <div
        style={{
          border: "1px solid #ccc",
          padding: "16px",
          borderRadius: "8px",
          display: "flex",
          flex: "1",
          flexDirection: "column",
          marginTop: "16px",
        }}
      >
        <Typography.Title
          level={5}
          style={{ marginBottom: "8px", color: "#1890ff" }}
        >
          {name}
        </Typography.Title>
        <Space style={{ justifyContent: "flex-end", marginTop: "8px" }}>
          <Button type="link" onClick={handleEdit}>
            <EditOutlined style={{ color: "#1890ff", fontSize: "16px" }} />
          </Button>
          <Button type="link" onClick={handleDelete}>
            <DeleteOutlined
              style={{ color: "#ff4d4f", fontSize: "16px" }}
            />
          </Button>
        </Space>
      </div>
      {/* <DeleteConfirmationModal
                isVisible={isDeleteModalVisible}
                onCancel={handleCancelDelete}
                onConfirm={handleConfirmDelete}
                modalInformation="Are you sure you want to delete this todo?"
            /> */}
    </>
  )
}
